import React from "react";
import Task from "../task";
import "./task-list.css";

// Define TodoItem interface
interface TodoItem {
  id: string;
  label: string;
  done: boolean;
  important?: boolean;
  isNewTask?: boolean;
  [key: string]: any; // For any additional properties
}

// Define component props interface
interface TaskListProps {
  todos: TodoItem[];
  onDeleted: (id: string) => void;
  onToggleImportant?: (id: string) => void;
  onToggleDone: (id: string) => void;
  onEdited: (id: string, editedLabel: string) => void;
}

const TaskList: React.FC<TaskListProps> = ({
  todos,
  onDeleted,
  onToggleImportant,
  onToggleDone,
  onEdited,
}) => {
  const elements = todos.map((item) => {
    const { id, ...itemProps } = item;
    return (
      <li key={id} className="list-group-item">
        <Task
          {...itemProps}
          onDeleted={() => onDeleted(id)}
          onEdited={(editedLabel: string) => onEdited(id, editedLabel)}
          onToggleImportant={
            onToggleImportant ? () => onToggleImportant(id) : undefined
          }
          onToggleDone={() => onToggleDone(id)}
          id={id} // Passing id back to Task component
        />
      </li>
    );
  });
  return <ul className="todo-list">{elements}</ul>;
};

export default TaskList;
