import React, { useState } from "react";
import AppHeader from "../app-header/";
import TaskList from "../task-list";
import TasksFilter from "../tasks-filter/taskFilter";
import NewTaskForm from "../new-task-form";
import Footer from "../app-footer";
import "./app.css";


interface TodoItem {
  label: string;
  important: boolean;
  done: boolean;
  id: string;
  isNewTask: boolean;
}

interface AppProps {
  initialTodoData?: TodoItem[];
  initialFilter?: string;
}

const App: React.FC<AppProps> = ({
  initialTodoData = [],
  initialFilter = "all",
}) => {
  const [todoData, setTodoData] = useState<TodoItem[]>(initialTodoData);
  const [term, setTerm] = useState<string>("");
  const [filter, setFilter] = useState<string>(initialFilter);

  const createTodoItem = (label: string): TodoItem => ({
    label,
    important: false,
    done: false,
    id: Date.now().toString(),
    isNewTask: true,
  });

  const deleteItem = (id: string): void => {
    setTodoData((todoData) => todoData.filter((item) => item.id !== id));
    localStorage.removeItem(`timer-${id}`);
    localStorage.removeItem(`timerActive-${id}`);
  };

  const addItem = (text: string): void => {
    const newItem = createTodoItem(text);
    setTodoData((todoData) => [...todoData, newItem]);
  };

  const editItem = (id: string, editedItem: string): void => {
    setTodoData((todoData) =>
      todoData.map((item) =>
        item.id === id ? { ...item, label: editedItem } : item,
      ),
    );
  };

  const toggleProperty = (
    arr: TodoItem[],
    id: string,
    propName: keyof TodoItem,
  ): TodoItem[] =>
    arr.map((item) =>
      item.id === id ? { ...item, [propName]: !item[propName] } : item,
    );

  const onToggleDone = (id: string): void => {
    setTodoData((todoData) => toggleProperty(todoData, id, "done"));
  };

  const onToggleImportant = (id: string): void => {
    setTodoData((todoData) => toggleProperty(todoData, id, "important"));
  };

  const search = (items: TodoItem[], term: string): TodoItem[] =>
    term.length === 0
      ? items
      : items.filter((item) =>
          item.label.toLowerCase().includes(term.toLowerCase()),
        );

  const filterItems = (items: TodoItem[], filter: string): TodoItem[] => {
    switch (filter) {
      case "all":
        return items;
      case "active":
        return items.filter((item) => !item.done);
      case "done":
        return items.filter((item) => item.done);
      default:
        return items;
    }
  };

  const onFilterChange = (newFilter: string): void => setFilter(newFilter);

  const onCleared = (): void =>
    setTodoData((todoData) => todoData.filter((item) => !item.done));

  const visibleItems = filterItems(search(todoData, term), filter);
  const doneCount = todoData.filter((el) => el.done).length;
  const todoCount = todoData.length - doneCount;

  return (
    <div className="todoapp">
      <AppHeader />
      <NewTaskForm addItem={addItem} />
      <TaskList
        todos={visibleItems}
        onDeleted={deleteItem}
        onEdited={editItem}
        onToggleDone={onToggleDone}
        onToggleImportant={onToggleImportant}
      />
      <div className="footer">
        <Footer count={todoCount} />
        <TasksFilter
          filter={filter}
          onFilterChange={onFilterChange}
          onCleared={onCleared}
        />
      </div>
    </div>
  );
};

export default App;
